import React from 'react';
import styled from 'styled-components';

const Hero = (props) => {
  return (
    <Container>
      <Img src={props.images.md} alt={props.name} />
      <Name>{props.name}</Name>
      <Name>{props.appearance.height[0]}</Name>
      <Name>{props.name}</Name>
    </Container>
  );
};

const Container = styled.div`
  width: 100%;
  -webkit-box-shadow: 0px 19px 19px -10px #ccc;  /* Safari 3-4, iOS 4.0.2 - 4.2, Android 2.3+ */
  -moz-box-shadow:    0px 19px 19px -10px #ccc;  /* Firefox 3.5 - 3.6 */
  box-shadow:         0px 19px 19px -10px #ccc;  /* Opera 10.5, IE 9, Firefox 4+, Chrome 6+, iOS 5 */
  transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  :hover {
  box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);
  }
  border-radius: 13px;
  background: #fff;
  padding:20px 10px;
  box-sizing:border-box;
  display: flex;
  flex-direction: column;
`;

const Img = styled.img`
  width:100%;
`;
const Category = styled.div`
  display: flex;
  justify-content:flex-end;
  >div {color: white;
  background: pink;
  display: inline;
  padding: 5px 10px 5px;
  border-radius: 20px;}
`;
const Name = styled.div`
  padding: .3em;
  font-size: 18px;
  color:black;
`;

const Prize = styled.div`
  padding: .3em;
  font-size: 22px;
`;

export default Hero;
